import React, { useEffect, useState } from 'react';


function App() {

  const [tipodata, setTipodata] = useState('post')



  return (
    <div className="App">
      <div>
        <button  onClick={ setTipodata('post') }> Post </button>
        <button  onClick={ setTipodata('users') }> Users </button>
        <button  onClick={ setTipodata('comments') }> Comments </button>
      </div>
      <h3>{ tipodata }</h3>
    </div>
  );
}

export default App;
