function App(){
  const [ancho, setAncho] = useState(window.innerWidth);

  const cambtamanio = () => {
    setAncho(window.innerWidth);
  };


  useEffect(() => {
    window.addEventListener("resize", cambtamanio);
  },[ancho]);

  return(
    <div>
      <h1> {ancho} </h1>
    </div>
  ) 
}

export default App;
